import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import AddReply from './AddReply'
import Reply from './Reply'
import { getReplies, replies } from '../../../utils/getCommentReply'

class CommentDetails extends Component {
  constructor() {
    super()
    this.state = {}
  }
  handleClick = () => {
    getReplies(this.props.projectId, this.props.comment.id)
    const id = this.props.comment.id
    console.log("rep: ", id)
    .then(() => {
      this.setState(() => {
        return {
          replies
        }
      }, () => {
        console.log("rep: ", this.state)
      })
    })
  }
  render() {
    const { comment, projectId } = this.props
    return(
      <div
        className="comments">
        <p>
          <Link to={'/profile/' + comment.commentFromId}>
            <span className="commentor green-text darken-1">
              {comment.commentFrom}
            </span>
          </Link>
          <span className="comment_02_spn">{comment.comment}</span>
        </p>
        <div>
          <AddReply
            commentId={comment.id}
            projectId={projectId}
            onClick={this.handleClick}/>
        </div>
        <div>
          <Reply />
        </div>
      </div>
    )
  }
}

export default CommentDetails
