import React, { Component} from 'react'
import { connect } from 'react-redux'
import { firestoreConnect } from 'react-redux-firebase'
import { compose } from 'redux'
import { Link } from 'react-router-dom'

class ProjectDetails extends Component {
  render() {
  const { project } = this.props
  if (project) {
    return (
      <div className="container section project-details">

        <div className="row">

          <div className="col s12 m8 offset-m2 l8 offset-l2">
            <div className="card z-depth-0 show-up">
              <div className="card-content">
                <span className="card-title"> {project.title} </span>
                <div className="card-image">
                  <img src={project.imageUrl} />
                  <p>{project.content}</p>
                </div>

              </div>

              <div className="card-action grey-text">

                <div>
                <Link to={'../profile/' + project.authorId} className="blue-text">
                  {project.authorFirstName} {project.authorSecondName}
                </Link>

                </div>
                <div>{ project.createdAt &&
                    project.createdAt.toDate()
                    .toLocaleDateString('indian',
                    {year: "2-digit", month: "short", day: "numeric"})}
                </div>

              </div>
            </div>
          </div>

        </div>

      </div>
    )
  } else {
    return (
      <div className="container center">
        <p> Loading projects. . . .  </p>
      </div>
    )
  }
}
}

const mapStateToProps = (state, ownProps) => {
  console.log(state)
  const id = ownProps.match.params.id
  const projects = state.firestore.data
  const project = projects ? projects[id] : null
  return {
    project: project
  }
}

export default compose(
  connect(mapStateToProps),
  firestoreConnect(props => {
    return [
     {
       collection: 'projects',
       doc: props.match.params.id,
       storeAs: props.match.params.id
     }
    ]
  })
)(ProjectDetails)

