import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Redirect } from 'react-router-dom'
import firebase from 'firebase/app'
import { createProject } from '../../store/actions/ProjectActions'
import AddToAlbum from './AddToAlbum'

class CreateProject extends Component {
  constructor() {
    super()
    this.contents = React.createRef()
    this.toggleDisplay = React.createRef()
    this.dropUp = React.createRef()
    this.dropDown = React.createRef()
      this.state = {
        title: '',
        content: '',
        imageUrl:'',
        progress: 0
      }
  }


  displayAlbum = () => {
    const targetEl = this.toggleDisplay.current
    const downArrow = this.dropDown.current
    const upArrow = this.dropUp.current

    console.log(targetEl)
    targetEl.classList.toggle('hide')
    downArrow.classList.toggle('hide')
    upArrow.classList.toggle('hide')
  }

  handleSubmit = (e) => {
    e.preventDefault()
    const contentRef = this.content.current
    const content = contentRef.value
    console.log(content)
    // const { uid } = this.props.auth
    // const message = "Please choose an image! Or if you want to write you ideas or thoughts post it on our blog"
    // const image = this.refs.image.files[0] ? this.refs.image.files[0] : alert(message)
    // const storage = firebase.storage()
    // const storageRef = storage.ref(`wallposts/${uid}` + image.name) // creating storage reference
    //
    // // inserting image to firestore
    // const uploadTask = storageRef.put(image)
    // uploadTask.on('state_changed',
    // (snapshot) => {
    //   // catch image upload progress
    //   const progress = (snapshot.bytesTransferred / snapshot.totalBytes) * 100
    //
    //   console.log('Upload is ' + progress + '% done')
    //   this.setState({progress})
    // },
    // (error) => {
    //   console.log(error)
    // },
    // () => {
    //   // getting download url for the image
    //   uploadTask.snapshot.ref.getDownloadURL()
    //   .then(url => {
    //     var imageUrl = url
    //     this.setState(() => {
    //       return {
    //         toUpload: {
    //           content: this.refs.content.value,
    //           imageUrl
    //         }
    //       }
    //     }, () => {
    //       this.props.createProject(this.state.toUpload)
    //       // this.props.history.push('/')
    //     })
    //   })
    // })
  }

  // getting image url to preview
  handleFile = (event) => {
    console.log("sdfsds")
    event.persist()
    const input = event.target
    console.log(event.target)
    const imageOriginalName = input.files[0]
    console.log(imageOriginalName)
     const reader = new FileReader()

     reader.onload = (e) => {
       const dataURL = reader.result
       console.log("img: ", e.target.result)

       this.setState(() => {
         return {
           preview: {imageUrl:dataURL}
         }
       }, () => {
         console.log("state: ", this.state)
       })
     }
     reader.readAsDataURL(input.files[0])
  }

  handleInputTrigger = () => {
    const ref = this.refs.image
    ref.click()
  }

  addToCategory = () => {
    console.log("works!")
  }

  render() {
    // getting uid from props
    const { auth } = this.props
    const { preview } = this.state
    if(!auth.uid) return <Redirect to='/signin' /> // redirecting signedOut user to signin page
    return(
      <div className="section">
        <div className="card z-depth-0 show-up post_create_wrapper">
          <div className="cp_ttl">
            <span> share your art </span>
          </div>
          <div className="preview_cover">

            {preview && preview.imageUrl
              ? <div className="preview">
                  <img
                    className="responsive-img"
                    src={preview.imageUrl}
                    alt="img"/>
                </div>
              : null}
          </div>

          <form className="post_create" onSubmit={this.handleSubmit}>
            <div className="thought">
              <div
                className="edt_wrap editable"
                contentEditable="true"
                data-placeholder="write thoughts to your art..."
                role="textbox"
                ref={this.contents}
                onKeyPress={this.handleKeyPress}>
              </div>
            </div>
            <div className="posting_optn">
              <div onClick={this.handleInputTrigger}>
                <i className="material-icons">
                    add_photo_alternate
                </i>
              </div>
              <div>
                <i
                  className="material-icons"
                  onClick={this.addToCategory}>
                    local_offer
                </i>
              </div>
                <div title="add to album" onClick={this.displayAlbum}>
                  <i className="material-icons">
                      photo_library
                      <i
                        className="material-icons arrow_down"
                        ref={this.dropDown}>
                        arrow_drop_down
                      </i>
                      <i
                        className="material-icons arrow_down hide"
                        ref={this.dropUp}>
                        arrow_drop_up
                      </i>
                  </i>
                </div>
              <input
                type="file"
                ref="image"
                className="add_post_img"
                onChange={this.handleFile} />
            </div>
            <div
              className="addtoalbum_wrap hide"
              ref={this.toggleDisplay}>
              <AddToAlbum />
            </div>
            {/*<div>
              <input type="text"/>
              <button className="btn z-depth-0 green">
                <i className="material-icons">check</i>
              </button>
            </div>*/}
            <div className="input-field submit_post">
              <button
                className="btn z-depth-0">
                Create
              </button>
            </div>
          </form>
            { this.state.progress !== 0
              ? <div className="upload-progress">
                  <label htmlFor="progress"> Sit back! File's being uploaded </label>
                  <progress id="progress" value={this.state.progress} max="100" />
                </div>
              : null}
        </div>
      </div>
    )
  }
}

// retrieving aut uid from firebase to store passing it as props
const mapStateToProps = (state) => {
  console.log(state)
  return {
    auth: state.firebase.auth
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    createProject: (project) => dispatch(createProject(project))
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(CreateProject)
