import React, { Component } from 'react'
import firebase from 'firebase/app'
import 'firebase/firestore'
import { connect } from 'react-redux'
import { compose } from 'redux'
import { firestoreConnect } from 'react-redux-firebase'
import { Redirect } from 'react-router-dom'
import Notification from './Notification'
import ProjectList from '../projects/ProjectList'
// import Footer from './Footer'
import Spinner from './Spinner'

class Dashboard extends Component {
  constructor() {
    super()
    this.lastVisible = ''
    this.state = {}
  }

  // TODO: get the posts manually and dispatch it
  // to the store
  // TODO: set data to local storage once component
  // successfully mounted

  componentDidMount() {
    this.getPosts()
  }

  getPosts = () => {
    const uid = this.props.auth.uid
    let post = []

    const db = firebase.firestore()
    // const followingRef = db.collection('users').doc(uid)
    //   .collection('following')

      return db.collection('projects')
        .where("authorId", "==", "IAjgGx0n2UPbmrl9scG9ocjOZCM2")
        .orderBy('createdAt', 'desc')
        .limit(3)
        .get().then((documentSnapshots) => {

          documentSnapshots.forEach((docs) => {
            let obj = {
              ...docs.data(),
              id: docs.id
            }
            post.push(obj)
            this.setState(() => {
              return {
                posts: {
                  ...post
                }
              }
            })
          })
        })
    // return followingRef.get().then(documentSnapshots => {
    //   documentSnapshots.forEach(doc => {
    //
    //     // retrieving "following" id to get related posts
    //     let data = doc.data()
    //     let uid = data.userid
    //     console.log("following: ", data, uid)
    //
    //     // getting posts of "folowing users"
    //     return db.collection('projects')
    //       .where('authorId', '==', uid)
    //       .get().then((querySnapshots) => {
    //
    //         querySnapshots.forEach((docs) => {
    //           let obj = {
    //             ...docs.data(),
    //             id: docs.id
    //           }
    //           post.push(obj)
    //           console.log("docs: ", post)
    //         })
    //       })
    //   })
    // })

  }

  render() {
  // console.log("project: ", this.state)
  console.log("docs: ", post)
    const { projects, auth, notifications } = this.props
    if (!auth.uid) return <Redirect to='/signin' /> // redirecting signed out users to signin/signup page

    if(projects && projects.length < 0) return <Spinner />
    return(
      <div className="dashboard container">
        <div className="row">
          <div className="col l8 m12 s12">
            {projects
              ? <ProjectList projects={projects} auth={auth}/>
              : null}
          </div>
          <div className="col l4 hide-on-med-and-down">
            <Notification notifications={notifications} />
          </div>
        </div>
        <div className="footer-cover">
        </div>
      </div>
    )
  }
}

const mapStateToProps = (state) => {
  // console.log(state)
  return {
    projects: state.firestore.ordered.projects,
    initials: state.firebase.profile.initials,
    auth: state.firebase.auth,
    notifications: state.firestore.ordered.notifications
  }
}

export default compose (
  firestoreConnect([
      { collection: 'projects',
        orderBy: ['createdAt', 'desc']
      },
      { collection: 'notifications',
        limit: 3,
        orderBy: ['time', 'desc']
      }
    ]),
  connect(mapStateToProps)
)(Dashboard)
