import React, { Component } from 'react'
import { connect } from 'react-redux'
import { createLike } from '../../../store/actions/LikeActions'
import share from '../../../static/icons/share.svg'

class PostCerdits extends Component {
  constructor() {
    super()
    this.state = {
      likeToPost: '',
    }
  }

  handleClick = () => {
    // console.log(this.props.likeCounts)
    this.setState(() => {
      return {
        likeToPost: this.props.projectId,
        likeCount: this.props.likeCount
      }
    }, () => {
      this.props.createLike(this.state)
    })
  }

  render() {
    const { likeCount } = this.props
    console.log(this.props.likeCount)
    return(
      <div>
      <div className="icons-wrapper">
        <div className="post-act">
          <span className="">
            <i className="material-icons thumb-up" onClick={this.handleClick}>
              thumb_up
            </i>
          </span>
          <span className="">
            <i className="material-icons"> mode_comment </i>
          </span>
          <span className="">
            <i>
              <img src={share} alt="share" height="27px" width="27px"/>
            </i>
          </span>
        </div>
        <div className="save">
          <span className="">
            <i className="material-icons center"
                title="add to your collection">
              bookmark_border
            </i>
          </span>
        </div>
      </div>
      <div className="counts">
          {likeCount
            ?
              <span className="like-count">
                {likeCount} likes
              </span>
            : null
          }
      </div>
      </div>
    )
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    createLike: (like) => dispatch(createLike(like))
  }
}


export default connect(null, mapDispatchToProps)(PostCerdits)
