import React, { Component } from 'react'

class SellNew extends Component {
    constructor() {
        super()
        this.state = { }
    }

    handleInputTrigger = () => {
        const ref = this.refs.image
        ref.click()
      }

    handleFile = (e) => {
        const ref = e.target
        const file = ref.files[0]
        const reader = new FileReader()

        reader.onload = () => {
            let url = reader.result
            this.setState(() => {
                return {
                    preview: url
                }
            }, () => {
                console.log("state: ", this.state)
            })
        }
        reader.readAsDataURL(file)
    }

    render() {
        return (
            <div className="sell_new_cover">
                <div className="sell_new_wrapper">
                    <form>
                        <input
                            className="add_post_img" 
                            type="file"
                            ref="image" 
                            onChange={this.handleFile} />
                            <span
                                className="button"
                                onClick={this.handleInputTrigger}> choose an image </span>
                            <div
                                className="img"
                                style={{ backgroundImage: `url(${this.state.preview})` }}></div>
                    </form>
                </div>
            </div>
        )
    }
}

export default SellNew
