import React, { Component } from 'react'
import { connect } from 'react-redux'
import { compose } from 'redux'
import { firestoreConnect } from 'react-redux-firebase'
// import MobileNavbar from '../layout/MobileNavbar'
// import StoreIntro from './StoreIntro'
import StoreProfile from './StoreProfile'

class Store extends Component {

    render() {
        // console.log(auth)
        const { auth, store } = this.props
        return (
            <div className="store_wrapper">

                <StoreProfile auth={auth} store={store} />

            </div>
        )
    }
}

const mapStateToProps = (state, ownProps) => {
    console.log(state)
    const id = ownProps && ownProps.match.params.id
    const store = state && state.firestore && state.firestore.ordered && state.firestore.ordered.store && state.firestore.ordered.store[id]
    return{
        auth: state.firebase.auth,
        store: store
    }
}

export default compose(
    firestoreConnect(props => {
        return [
            {
                collection: 'stores',
                where: [
                    ['ownerId', '==', props.match.params.id]
                  ]
            }
        ]
    }),
    connect(mapStateToProps)
)(Store)
