import React, { Component } from 'react'
import { connect } from 'react-redux'
import firebase from 'firebase/app'
// import 'firebase/firestore'
// import { firestoreConnect } from 'react-redux-firebase'
// import { compose } from 'redux'
import { createLike } from '../../../store/actions/LikeActions'

class PostCerdits extends Component {
  constructor() {
    super()
    this.state = {
      likeToPost: '',
    }
  }

  handleClick = () => {
    // console.log(this.props.projectId)
    this.setState(() => {
      return {
        likeToPost: this.props.projectId
      }
    }, () => {
      this.props.createLike(this.state)
    })
  }

  render() {

    var likes = 0
    const db = firebase.firestore()
    const docRef = db.collection('projects').doc(this.props.projectId)
                  .collection('likes')
    docRef.get().then((querySnapshot) => {
      querySnapshot.forEach((doc) => {
        likes++
        console.log(likes)
      })
    })

    // const { project } = this.props
    return(
      <div className="icons-wrapper">
        <div className="post-act">
          <span className="">
            <i className="material-icons" onClick={this.handleClick}> thumb_up </i>
            <span className="like-count">
              {likes}
            </span>
          </span>
          <span className="">
            <i className="material-icons"> mode_comment </i>
          </span>
          <span className="">
            <i className="material-icons center"> send </i>
          </span>
        </div>
        <div className="save">
          <span className="">
            <i className="material-icons center"
                title="add to your collection">
              bookmark_border
            </i>
          </span>
        </div>
      </div>
    )
  }
}

const mapStateToProps = (state) => {
  // console.log("state: ", state)
  return {

  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    createLike: (like) => dispatch(createLike(like))
  }
}

