import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Redirect, /*Link*/ } from 'react-router-dom'
import { firestoreConnect } from 'react-redux-firebase'
import { compose } from 'redux'
import ProfilePicture from './ProfilePicture'
import Bio from './TabContents/Bio'
import Followers from './TabContents/Followers'
import ArtWork from './TabContents/ArtWorks'
import Collections from './TabContents/Collections'

class Profile extends Component {
  constructor() {
    super()
    this.state = { }
  }

  handleClick = (item) => {
    this.setState(() => {
      return {
        item
      }
    })
  }

  render() {
    const { auth } = this.props
    // const { profile } = this.props
    const { item } = this.state
        // console.log("par: ", this.props.match.params.id)
    if(!auth.uid) return <Redirect to='/signin' />

    let tab
    if(item === 'Followers') {
      tab = <Followers />
    } else if(item === 'Bio') {
        tab = <Bio />
    } else if(item === 'Collections') {
      tab = <Collections collection={this.props.collection} />
    } else {
      tab = <ArtWork artWork={this.props.artWorks} />
    }

    return(
      <div className="profile-wrapper">
        <div className="row">

          <div className="col l12 m12 s12">

            <div>
              <ProfilePicture
                onClick={this.handleClick}
                uid={this.props.auth.uid}
                user={this.props.user}
                />
            </div>

          </div>

          <div className="tab-content-wrapper">
            <div className="col l4 m4 s12">
              <span className="">
                <i className="material-icons">note_add</i>
              </span>
              <span className="">
                <i className="material-icons">person_add</i>
              </span>
            </div>
            <div className="row content">
              <div className="col l8 m8 s12">
                {tab}
              </div>
            </div>
          </div>

        </div>
      </div>
    )
  }
}

const mapStateToProps = (state) => {
  console.log("state: ", state.firestore.ordered.user)
  const userPic = state.firestore.ordered.user ? state.firestore.ordered.user : null
  return {
    auth: state.firebase.auth,
    profile: state.firebase.profile,
    artWorks: state.firestore.ordered.artWork,
    collection: state.firestore.ordered.collection,
    user: userPic
  }
}

export default compose (
  firestoreConnect(props => {
    return [
      {
        collection: 'projects',
        where: [
          ['authorId', '==', props.match.params.id]
        ],
        storeAs: 'artWork',
        orderedBy: ['createdAt', 'desc']
      },
      {
        collection: 'users',
        doc: props.match.params.id,
        subcollections: [
          {
            collection: 'collection'
          }
        ],
        storeAs: 'collection',
        orderedBy: ['time', 'desc']
      },
      {
        collection: 'users',
        doc: props.match.params.id,
        storeAs: 'user'
      }
    ]
  }),
  connect(mapStateToProps)
)(Profile)

// export default Profile
