export const createProject = (project) => {
  return (dispatch, getState , { getFirebase, getFirestore }) => {

    // adding data to firestore
    const firestore = getFirestore()
    const firebase = getFirebase()
    const profile = getState().firebase.profile // retreiving user profile info
    const userId = getState().firebase.auth.uid // accessing user's user ID

    const image = project.imagePath // getting file
    const storageRef = firebase.storage().ref('wallposts/' + image) // creating storage reference
    const task = storageRef.put(image) // inserting image to firebase
    firestore.collection('projects').add({
      ...project,
      authorFirstName: profile.firstName,
      authorSecondName: profile.lastName,
      authorId: userId,
      createdAt: new Date()
    }).then(() => {
      dispatch({type:'CREATE_PROJECT', project })
    }).catch((err) => {
      dispatch({type:'CREATE_PROJECT_ERR', err })
    })
  }
}
