import React, { Component } from 'react'
import portrait from '../../../static/portrait.png'

class EditProfile extends Component {
  constructor() {
    super()
    this.state = {}
  }
  // close modal
  closeModale = () => {
    this.props.onClick()
  }

  // trigger input/file
  handleTrigger = () => {
    const ref = this.refs.picture
    ref.click()
  }

  // hanlde file change
  handleChange = (event) => {
    event.persist()
    const input = event.target;
    const imageOriginalName = input.files[0].name
    const reader = new FileReader()
     reader.onload = () => {
       const dataURL = reader.result

       this.setState(() => {
         return {
           imageSrc: dataURL,
           imageName: imageOriginalName
         }
       })
     }
     reader.readAsDataURL(input.files[0])
  }

  render() {
    const { profile, auth } = this.props

    return(
      <div className="modal_wrapper">
        <form action="">

          <div className="modal_cont white">
            <i
              className="material-icons close"
              onClick={this.closeModale}>close</i>
            <div className="edit_pic">
              <div className="picture_wrapper">
                <i
                  className="material-icons"
                  onClick={this.handleTrigger}>
                    create
                </i>
                { !this.state.imageSrc
                    ? profile && profile.pictureUrl
                      ? <img
                          src={profile.pictureUrl}
                          alt="dp"
                          height="150px"
                          width="150px" />
                      : <img
                          src={portrait}
                          alt="dp"
                          height="150px"
                          width="150px" />
                    : <img
                        src={this.state.imageSrc}
                        alt="dp"
                        height="150px"
                        width="150px" />
                      }
              </div>
              <div className="edit_username">
                <label htmlFor="fullname"> Full Name </label>
                <input type="text" ref="fullname" placeholder="enter your name..." />
              </div>
              <div className="hide">
                <input
                  type="file"
                  ref="picture"
                  onChange={this.handleChange}  />
              </div>
            </div>
                    <div className="devider"></div>
            <div>

            </div>
          </div>
        </form>
      </div>
    )
  }
}

export default EditProfile
