import React, { Component } from 'react'
import { connect } from 'react-redux'
// import { Redirect } from 'react-router-dom'
import { firestoreConnect } from 'react-redux-firebase'
import { compose } from 'redux'
import ProfileHeader from './ProfileHeader'
import ProfilePicture from './ProfilePicture'

class Profile extends Component {
  constructor() {
    super()
    this.state = {}
  }

  render() {
    const { auth, profile } = this.props
    return(
      <div className="profile-wrapper">
        <div className="container">
          <div className="header">
            { auth && profile
              ? <ProfileHeader
                  auth={auth}
                  profile={profile} />
              : null }
          </div>
          <div className="row">
            <div className="col l12 m12 s12">
              { profile
                ? <ProfilePicture profile={profile} />
                : null }
            </div>
          </div>
        </div>
      </div>
    )
  }
}

const mapStateToProps = (state, ownProps) => {
  const profileStore = state.firestore.ordered.userProfile
  const profile = profileStore ? profileStore[0] : null
  // console.log("profile: ", profile)
  // console.log(state)
  return {
    auth: state.firebase.auth,
    profile: profile,
    artWorks: state.firestore.ordered.artWork,
    collection: state.firestore.ordered.collection,
    following: state.firestore.ordered.following,
    followers: state.firestore.ordered.followers
  }
}

export default compose (
  firestoreConnect(props => {
    return [
      {collection: 'projects',
        where: [
          ['authorId', '==', props.match.params.id]
        ],
        storeAs: 'artWork',
        orderedBy: ['createdAt', 'desc']},

      {collection: 'users',
        doc: props.match.params.id,
        subcollections: [{
          collection: 'collection'
        }],
        storeAs: 'collection',
        orderedBy: ['time', 'desc']},

      {collection: 'users',
        doc: props.match.params.id,
        storeAs: 'userProfile'},

      {collection: 'users',
        doc: props.match.params.id,
        subcollections: [{
          collection: 'following'
        }],
        storeAs: 'following'},

        {collection: 'users',
          doc: props.match.params.id,
          subcollections: [{
            collection: 'followers'
          }],
          storeAs: 'followers'
        }
    ]
  }),
  connect(mapStateToProps)
)(Profile)

// export default Profile
