import React from 'react'

const ProfileDetails = ({ format, onClick }) => {
  console.log("format: ",format)
  function handleClick(e) {
    onClick()
    e.target.closest("span").contentEditable = true
    console.log(e.target)
  }
  return(
    <div className="bio" data-target="#details">
      <span  id="name"> User Name <i className="material-icons edit right" contentEditable="false" data-target="#name" onClick={handleClick}>edit</i> </span>
      <span contentEditable={format.canEdit} id="uName"> Default User <i className="material-icons edit right" contentEditable="false" data-target="#name" onClick={handleClick}>edit</i> </span>
      <span contentEditable={format.canEdit} id="phNo"> 948294571 <i className="material-icons edit right" contentEditable="false" data-target="#name" onClick={handleClick}>edit</i> </span>
      <span contentEditable={format.canEdit} id="email"> madanhegde99@gmail.com <i className="material-icons edit right" contentEditable="false" data-target="#name" onClick={handleClick}>edit</i> </span>
    </div>
  )
}

export default ProfileDetails
