import React, { Component } from 'react'
import portrait from '../../../static/portrait.png'

class EditProfile extends Component {
  constructor() {
    super()
    this.state = {}
  }

  // trigger input/file
  handleTrigger = () => {
    const ref = this.refs.picture
    ref.click()
  }

  // hanlde file change
  handleChange = (event) => {
    event.persist()
    const input = event.target;
    const imageOriginalName = input.files[0].name
    const reader = new FileReader()
     reader.onload = () => {
       const dataURL = reader.result

       this.setState(() => {
         return {
           imageSrc: dataURL,
           imageName: imageOriginalName
         }
       })
     }
     reader.readAsDataURL(input.files[0])
  }

  render() {
    const { profile, auth } = this.props

    return(
      <div className="modal_wrapper">
        <form action="">
          <div className="modal_cont white">
            <div className="edit_pic">
              <div>
                <i
                  className="material-icons"
                  onClick={this.handleTrigger}>
                    create
                </i>
                { !this.state.imageSrc
                    ? profile && profile.pictureUrl
                      ? <img
                          src={profile.pictureUrl}
                          alt="dp"
                          height="150px"
                          width="150px" />
                      : <img
                          src={portrait}
                          alt="dp"
                          height="150px"
                          width="150px" />
                    : <img
                        src={this.state.imageSrc}
                        alt="dp"
                        height="150px"
                        width="150px" />
                      }
              </div>
              <div className="edit_username">
                <input type="text" placeholder="user name" />
              </div>
              <input
                type="file"
                ref="picture"
                onChange={this.handleChange}  />
            </div>
          </div>
        </form>
      </div>
    )
  }
}

export default EditProfile
