import React, { Component } from 'react'
import ProjectList from '../projects/ProjectList'
import { connect } from 'react-redux'
import { compose } from 'redux'
import { firestoreConnect } from 'react-redux-firebase'
import { Redirect } from 'react-router-dom'
import Notification from './Notification'
import Footer from './Footer'
import { getData } from '../../utils/GetSortedPost' // retrieving data from cloud firestore
import { allPosts } from '../../utils/GetSortedPost' // getting final data of posts
import { passPostToStore } from '../../store/actions/postsToStore'

class Dashboard extends Component {
  constructor() {
    super()
    this.state = {}
  }

  // componentWillMount() {
  //   //console.log("comp")
  //   this.setState({projects: null})
  //   getData()
  //   .then(() => {
  //     console.log("see", allPosts)
  //     this.setState(() => {
  //       return{
  //         projects: [...allPosts]
  //       }
  //     }, () => {
  //       this.props.passPostToStore(this.state)
  //     })
  //   })
  // }

  render() {
    const { projects, auth, notifications } = this.props
    if (!auth.uid) return <Redirect to='/signin' /> // redirecting signed out users to signin/signup page
    return(
      <div className="dashboard container">
        <div className="row">
          <div className="col l8 m12 s12">
            <ProjectList projects={projects} auth={auth}/>
          </div>
          <div className="col l4 hide-on-med-and-down">
            <Notification notifications={notifications} />
          </div>
        </div>
        <div className="footer-cover">
          <Footer />
        </div>
      </div>
    )
  }
}

// const mapDispatchToProps = (dispatch) => {
//   return {
//     passPostToStore: (posts) => dispatch(passPostToStore(posts))
//   }
// }

const mapStateToProps = (state) => {
  console.log(state)
  return {
    projects: state.firestore.ordered.projects,
    initials: state.firebase.profile.initials,
    auth: state.firebase.auth,
    notifications: state.firestore.ordered.notifications
  }
}

export default compose (
  firestoreConnect([
      { collection: 'projects',
        orderBy: ['createdAt', 'desc']
      },
      { collection: 'notifications',
        limit: 3,
        orderBy: ['time', 'desc']
      }
    ]),
  connect(mapStateToProps)
)(Dashboard)
