import React, { Component } from 'react'
import { connect } from 'react-redux'
// import { Redirect } from 'react-router-dom'
import { firestoreConnect } from 'react-redux-firebase'
import { compose } from 'redux'
import ProfileHeader from './Components/ProfileHeader'
import ProfilePicture from './Components/ProfilePicture'
import ProfileContent from './ProfileContent'
import Tabs from './Tabs'

class Profile extends Component {
  constructor() {
    super()
    this.state = {}
  }

  handleClick = (tab) => {
    this.setState(() => {
      return {
        selectedTab: tab
      }
    }, () => {
      console.log(this.state)
    })
  }

  render() {
    const { auth, profile, artWorks } = this.props
    return(
      <div className="profile-wrapper">

          <div className="picture_basic">
            <div className="header">
              { auth && profile
                ? <ProfileHeader
                    auth={auth}
                    profile={profile} />
                : null }
            </div>
            <div className="container">
              <div className="row">
                <div className="card z-depth-0">

                  <div className="col l10 m12 s12 white">
                    { profile
                      ? <ProfilePicture profile={profile} auth={auth} />
                      : null }
                    <div className="devider"></div>
                    <div className="tab_sec">
                      <Tabs onClick={this.handleClick} />
                    </div>
                  </div>

                  <div className="col l10 m12 s12 white">
                    { artWorks && artWorks.length > 0
                      ? <ProfileContent artWorks={artWorks} />
                      : <div className="no_post">
                          <span> <i>Has not posted anything yet!</i> </span>
                        </div> }
                  </div>

                </div>
              </div>
            </div>
          </div>

      </div>
    )
  }
}

const mapStateToProps = (state, ownProps) => {
  const profileStore = state.firestore.ordered.userProfile
  const profile = profileStore ? profileStore[0] : null
  // console.log("profile: ", profile)
  // console.log(state)
  return {
    auth: state.firebase.auth,
    profile: profile,
    artWorks: state.firestore.ordered.artWork,
    collection: state.firestore.ordered.collection,
    following: state.firestore.ordered.following,
    followers: state.firestore.ordered.followers
  }
}

export default compose (
  firestoreConnect(props => {
    return [
      { collection: 'projects',
        orderBy: ['createdAt', 'desc'],
        where: [
          ['authorId', '==', props.match.params.id]
        ],
        storeAs: 'artWork'},

      { collection: 'users',
        doc: props.match.params.id,
        subcollections: [{
          collection: 'collection'
        }],
        storeAs: 'collection',
        orderBy: ['time', 'desc']},

      { collection: 'users',
        doc: props.match.params.id,
        storeAs: 'userProfile'},

      { collection: 'users',
        doc: props.match.params.id,
        subcollections: [{
          collection: 'following'
        }],
        storeAs: 'following'},

      { collection: 'users',
        doc: props.match.params.id,
        subcollections: [{
          collection: 'followers'
        }],
        storeAs: 'followers'
      }
    ]
  }),
  connect(mapStateToProps)
)(Profile)

// export default Profile
