import React from 'react'
import moment from 'moment'
import { Link } from 'react-router-dom'

const CommentDetails = ({ comments }) => {
  // console.log(comments)
  return(
    <div>
      {
        comments && comments.map((comment) => {
          return(
            <div key={comment.id}>

              <Link to={'../profile/' + comment.commentFromId}
                className="green-text">
                <span>{comment.commentFrom}</span>
              </Link>

              <span className="black-text">{comment.comment}</span>
              <span className="grey-text lighten-5 right">
                {moment(comment.commentTime.toDate()).fromNow()}
              </span>
            </div>
          )
        })
      }
    </div>
  )
}

export default CommentDetails
