import React, { Component } from 'react'
import { connect } from 'react-redux'
// import { Redirect } from 'react-router-dom'
import { firestoreConnect } from 'react-redux-firebase'
import { compose } from 'redux'
import ProfileNav from './Components/ProfileNav'
import ProfileContent from './ProfileContent'
import EditProfile from './Components/EditProfile'

class Profile extends Component {
  constructor() {
    super()
    this.state={
      style: {
        display: 'none'
      }
    }
  }

  // hide edit profile modal
  closeModal = () => {
    this.setState({style:{display: 'none'}})
  }

  // display edit profile modal
  displayModal = () => {
    this.setState({style:{display: 'block'}})
  }

  render() {
    const { auth, profile, artWorks } = this.props
    return(
      <div className="profile-wrapper">
        <div style={this.state.style}>
          <EditProfile
          auth={auth}
          profile={profile}
          onClick={this.closeModal}/>
        </div>
          <div className="picture_basic">
            <div className="navbar">
              { auth && profile
                ? <ProfileNav
                    auth={auth}
                    profile={profile} />
                : null }
            </div>
            <div className="container">
              <div className="row">

                <div className="col l12 m12 s12">
                  <ProfileContent
                    profile={profile}
                    auth={auth}
                    artWorks={artWorks}
                    onClick={this.displayModal}/>
                </div>

              </div>
            </div>
          </div>
      </div>
    )
  }
}

const mapStateToProps = (state, ownProps) => {
  const profileStore = state.firestore.ordered.userProfile
  const profile = profileStore ? profileStore[0] : null
  // console.log("profile: ", profile)
  // console.log(state)
  return {
    auth: state.firebase.auth,
    profile: profile,
    artWorks: state.firestore.ordered.artWork,
  }
}

export default compose (
  firestoreConnect(props => {
    return [
      { collection: 'projects',
        orderBy: ['createdAt', 'desc'],
        where: [
          ['authorId', '==', props.match.params.id]
        ],
        storeAs: 'artWork'},

      { collection: 'users',
        doc: props.match.params.id,
        storeAs: 'userProfile'}
    ]
  }),
  connect(mapStateToProps)
)(Profile)

// export default Profile
