export const uploadProfilePicture = (pictureUrl) => {
  return(dispatch, getState, { getFirebase, getFirestore }) => {

    const firestore = getFirestore()
    const userId = getState().firebase.auth.uid

    firestore.collection('users').doc(userId)
    .update({
      ...pictureUrl
    })
  }
}
