import firebase from 'firebase/app'
import 'firebase/firestore'

export const getData = () => {

  const db = firebase.firestore()
  const atFirstCall = db.collection('projects')
    .orderBy('createdAt')
    .limit(15)
  var allPosts = []

    return atFirstCall.get().then((documentSnapshots) => {
      // Get the last visible document
      var lastVisible = documentSnapshots.docs[documentSnapshots.docs.length-1];
      // console.log("last", documentSnapshots);
      documentSnapshots.forEach(doc => {
        let obj = {
          ...doc.data(),
          id: doc.id
        }
        allPosts.push(obj)
      })
      // Construct a new query starting at this document,
      // get the next 25 cities.
      // var next = db.collection("cities")
      //     .orderBy("population")
      //     .startAfter(lastVisible)
      //     .limit(25);

    })
    .then(() => {
      console.log(allPosts)
      dispatch({ type: 'RETRIEVE_POST', allPosts })
    })
    .catch(error => {
      dispatch({ type: 'RETRIEVE_POST_ERR', error })
      console.log("something went wrong! Please refresh the page", error)
    })
}
