import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import AddReply from './Reply'

class CommentDetails extends Component {

  handleClick = (e) => {
    const el = e.target.closest('div')
    const targetEl = el.previousSibling
    // const closeTag =
    console.log("el: ", el)
    console.log("tEl: ", targetEl)
    targetEl.style.display = 'block'
    el.style.display = 'block'
  }

  handleFocus = (e) => {
    console.log("focus:", e)
  }

  render() {
    const { comment } = this.props
    return(
      <div
        className="comments">
        <p>
          <Link to={'/profile/' + comment.commentFromId}>
            <span className="commentor green-text darken-1">
              {comment.commentFrom}
            </span>
          </Link>
          <span className="comment_02_spn">{comment.comment}</span>
        </p>
        <div>
          <AddReply />
        </div>
      </div>
    )
  }
}

export default CommentDetails
