import React, { Component } from 'react'

class SellNew extends Component {
    constructor() {
        super()
        this.state = {
            style: {
                display: 'none'
            }
        }
    }

    handleInputTrigger = () => {
        const ref = this.refs.image
        ref.click()
      }

    handleFile = (e) => {
        const ref = e.target
        const file = ref.files[0]
        const reader = new FileReader()

        reader.onload = () => {
            let url = reader.result
            this.setState(() => {
                return {
                    preview: url
                }
            }, () => {
                this.setState({style:{display:"block"}})
            })
        }
        reader.readAsDataURL(file)
    }

    closeForm = () => {
        this.props.onClick()
    }

    render() {
        return (
            <div className="sell_new_cover">
                <i className="material-icons"
                    onClick={() => this.closeForm()}> close </i>
                <div className="sell_new_wrapper">
                    <form>
                        <input
                            className="add_post_img" 
                            type="file"
                            ref="image" 
                            onChange={this.handleFile} />
                        <span
                            className="button"
                            onClick={this.handleInputTrigger}> choose an image </span>
                        <div 
                            style={this.state.style}
                            className="image_preview">
                            <img 
                                src={this.state.preview} 
                                alt="art"/>
                        </div>

                       <div className="info">
                            <label> Give this a title </label>
                            <input id="title" type="text" placeholder="title, here" />

                            <label> call a basic price </label> 
                            <input type="text" placeholder="cost, here" />

                            <label> size of this art in any units </label>
                            <div className="measure">
                                <input type="number" />
                                <select name="units">
                                    <option value="inch"> in </option>
                                    <option value="feet"> ft </option>
                                    <option value="centimeter"> cm </option>
                                </select>
                            </div>
                       </div>

                        <lable> Description </lable>
                        <textarea></textarea>

                        <button type="submit"> Publish </button>

                    </form>
                </div>
            </div>
        )
    }
}

export default SellNew
