import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import { connect } from 'react-redux'

class Comments extends Component {
  constructor() {
    super()
    this.displayReply = React.createRef()
    this.state = {
      style: {
        display: 'none'
      }
    }
  }

  handleClick = () => {
    const el = this.displayReply.current
    console.log(el)
    // this.setState({
    //   style: {
    //     display: 'block'
    //   }
    // })
  }

  render(){
  // console.log("comments: ", comments)
    const { comments } = this.props
    return(
      <div>
        <div className="loaded-comments">
          {comments && comments.map((comment) => {
            return (
              <div
                key={comment.id}
                className="comments">
                <p>
                  <Link to={'/profile/' + comment.commentFromId}>
                    <span className="commentor green-text darken-1">
                      {comment.commentFrom}
                    </span>
                  </Link>
                  <span className="comment_02_spn">{comment.comment}</span>
                </p>

                <form
                  className="reply"
                  style={this.state.style}
                  ref={this.displayReply}>
                  {/*<label htmlFor="reply"> reply </label>
                  <input id="reply" type="text" placeholder="give them a reply..." />*/}
                  <div>

                    <input
                      id="icon_prefix"
                      type="text"
                      className="validate"
                      placeholder="write a reply..."/>
                    <i className="material-icons prefix right-align">send</i>

                  </div>
                </form>
                <span
                  className="repl_btn"
                  onClick={this.handleClick}>reply</span>

                <div className="devider"></div>
              </div>
            )
          })}
        </div>
      </div>
    )
  }
}
const mapStateToProps = (state, ownProps) => {
  // console.log("state: ", state)
  return {
    // profile:
  }
}


export default connect(mapStateToProps)(Comments)
