import React, { Component } from 'react'
import { firestoreConnect } from 'react-redux-firebase'
import { compose } from 'redux'
import { connect } from 'react-redux'
import firebase from 'firebase/app'
import 'firebase/firestore'
import { createLike, removeLike, updateLike } from '../../../store/actions/LikeActions'
import { addCollection } from '../../../store/actions/AddToCollection'

class PostCerdits extends Component {
  constructor() {
    super()
    this.state = {}
  }

  componentWillMount() {
    const db = firebase.firestore()
    const ref = db.collection('projects').doc(this.props.project.id)
      .collection('likes')
      .where('likeFromId', '==', this.props.project.authorId)

    return ref.get().then((documentSnapshots) => {
      documentSnapshots.forEach(doc => {
        let obj = {
          like: {
            ...doc.data(),
            id: doc.id
          }
        }
        this.setState({
          ...obj
        }, () => {
          console.log("thisss: ", this.state.like)
        })
      })
    })
  }

  // incrementing like to the post
  handleLike = () => {
    let likes = this.props.likeCount + 1
    const like = this.state.like ? this.state.like : null
    const isLiked = like && like.isActive ? true : false
    console.log("likeee: ", likes)

    like
    ? isLiked
      ? this.setState(() => {
          return {
            like: {
              likeToPost: this.props.project.id,
              likeCount: likes - 2,
              id: like.id
            }
          }
        }, () => {
          console.log(this.state.like)
          this.props.removeLike(this.state.like)
        })

      : this.setState(() => {
          return {
            like: {
              likeToPost: this.props.project.id,
              likeCount: likes,
              isActive: true
            }
          }
        }, () => {
          this.props.updateLike(this.state.like)
        })

    : this.setState(() => {
        return {
          like: {
            likeToPost: this.props.project.id,
            likeCount: likes,
            isActive: true
          }
        }
      }, () => {
        this.props.createLike(this.state.like)
      })
  }

  // adding post to user's collection
  addCollection = () => {
    let collectedCount = this.props.collected + 1
    let activeStatus = this.props.isActive ? false : true

    this.setState(() => {
      return {
        imageUrl: this.props.project.imageUrl,
        postId: this.props.project.id,
        activeStatus,
        collectedCount
      }
    }, () => {
      this.props.addCollection(this.state)
    })
  }

  render() {
    const { likeCount, auth } = this.props
    const { like } = this.state
    const liked = like && like.isActive ? true : false
    //  console.log("l:", liked)

    return(
      <div>
      <div className="icons-wrapper">
        <div className="post-act">
          <span className="">
            <button onClick={this.handleLike}>
              {liked
                ? <i className="material-icons blue-text">thumb_up</i>
                : <i className="material-icons">thumb_up</i>
              }
            </button>
          </span>
          <span className="">
            <button>
              <i className="material-icons">send</i>
            </button>
          </span>
        </div>
        <div className="save">
          <span className="">
            <button onClick={this.addCollection}>
               <i className="material-icons center"
                    title="add to your collection">
                  add_to_photos
                </i>
            </button>
          </span>
        </div>
      </div>
        {likeCount
          ?
            <div className="counts">
              <span className="like-count">
                {likeCount} likes
              </span>
            </div>
          : null
        }
      </div>
    )
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    createLike: (like) => dispatch(createLike(like)),
    removeLike: (like) => dispatch(removeLike(like)),
    updateLike: (like) => dispatch(updateLike(like)),
    addCollection: (post) => dispatch(addCollection(post))
  }
}


export default connect(null, mapDispatchToProps)(PostCerdits)
