import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Redirect } from 'react-router-dom'
import portrait from '../../static/portrait.JPG'

class Profile extends Component {
  constructor() {
    super()
    this.state = {

    }
  }
  render() {
    const { auth } = this.props
    const { profile } = this.props
    if(!auth.uid) return <Redirect to="/signin" />
    return(
      <div className="container">
        <div className="row">

          <div className="profile col l12 m10 offset-m1 s12 white">
            <div className="profile-image">
              <img src={portrait} alt="portrait" className="circle responsive-img" height="200" width="200" />
              <span className="user-name"> {`${profile.firstName} ${profile.lastName}`}  </span>
            </div>
          </div>
          <div className="col l12 m10 offset-m1 s12 white">
            <div class="input-field">
              <input placeholder="Placeholder" id="first_name" type="text" class="validate" />
              <label for="first_name">First Name</label>
            </div>
          </div>

        </div>
      </div>
    )
  }
}

const mapStateToProps = (state) => {
  console.log("for: ", state)
  return {
    auth: state.firebase.auth,
    profile: state.firebase.profile
  }
}

export default connect(mapStateToProps)(Profile)
