import React from 'react'

const ProfileDetails = ({ format, onClick }) => {
  console.log("format: ",format)
  function handleClick(e) {
    onClick()
    e.target.closest("span").contentEditable = true
    e.target.closest("span").classList.toggle("editable")
  }
  return(
    <div className="bio" data-target="#details">
      <span id="name"> <i className="material-icons">account_circle</i> User Name <i className="material-icons edit right" contentEditable="false" data-target="#name" onClick={handleClick}>edit</i> </span>
      <span id="uName"> <i className="material-icons">account_circle</i> Default User <i className="material-icons edit right" contentEditable="false" data-target="#name" onClick={handleClick}>edit</i> </span>
      <span id="phNo"> <i className="material-icons">phone</i> 948294571 <i className="material-icons edit right" contentEditable="false" data-target="#name" onClick={handleClick}>edit</i> </span>
      <span id="email"> <i className="material-icons">account_circle</i> madanhegde99@gmail.com <i className="material-icons edit right" contentEditable="false" data-target="#name" onClick={handleClick}>edit</i> </span>
    </div>
  )
}

export default ProfileDetails
