import React, { Component } from 'react'
import firebase from 'firebase/app'
import ReactCrop from 'react-image-crop'
import 'react-image-crop/dist/ReactCrop.css';
import portrait from '../../static/portrait.png'

class UploadImage extends Component {
  constructor() {
    super()
    this.state = {
      imageSrc: portrait,
      crop: {
        aspect: 1/1
      }
    }
  }



  render() {
    return(
      <div className="modal-wrapper">
        <div className="image-modal">
          <div className="photo">
            <img src={this.state.imageSrc} alt="dp" className="responsive-img"/>
            <ReactCrop src={this.state.imageSrc} crop={this.state.crop} />
          </div>
          <div className="select-file center">
            <form>
              <input type="file" />
              <button type="submit" className="btn indigo z-depth-0">
                Upload
              </button>
            </form>
          </div>
        </div>
      </div>
    )
  }
}

export default UploadImage
