import React , { Component } from 'react'
import unreal from '../../static/unreal.jpg'

class AddToAlbum extends Component {
  constructor() {
    super()
    this.newAlbum = React.createRef()
    this.state={
      className: 'unselect'
    }
  }

  switchClass = () => {
    if(this.state.className === 'unselect') {
      this.setState({
        className: 'select',
        select: true
      })
    } else {
      this.setState({
        className: 'unselect',
        select: false
      })
    }
  }

  handleClick = () => {
    const ref = this.newAlbum.current
    const albumName = ref.innerText
    console.log("album: ", albumName)
  }

  render() {
    return(
      <div className="addtoalbum">
        <div>
          <div className="album_list">
            <div>
              <img src={unreal} alt="img"/>
              <span> nature </span>
              <span
                className={`${this.state.className}`}
                onClick={this.switchClass}>
                <i className="material-icons">check</i>
              </span>
            </div>
          </div>
          <div className="form">
            <span
              className="new_alb editable"
              ref={this.newAlbum}
              contentEditable="true"
              data-placeholder="new album...">
              </span>
              <button
                className="btn z-depth-0 green"
                onClick={this.handleClick}>
                <i className="material-icons">check</i>
              </button>
          </div>
        </div>
      </div>
    )
  }
}

export default AddToAlbum
