import React, { Component } from 'react'
import { connect } from 'react-redux'
import { compose } from 'redux'
import { firestoreConnect } from 'react-redux-firebase'

class Comments extends Component {

  render() {
    const { comments } = this.props
    return(
      <div>
        <div className="loaded-comments">
          <span className="card-title grey-text text-darken-4"> {comments.comment} <i className="material-icons right">close</i></span>
          <p> <span className="commentor"> Joe </span> Beautiful!!! </p>
        </div>
      </div>
    )
  }
}

const mapStateToProps = (state) => {
  return {
    comments: state.firestore.data.comments
  }
}

export default compose(
    connect(mapStateToProps),
    firestoreConnect([
      { collection: 'comments' }
    ])
)(Comments)
