import React, { Component } from 'react'
import { firestoreConnect } from 'reac-redux-firebase'
import { connect } from 'react-redux'
import { createLike } from '../../../store/actions/LikeActions'
import { addCollection } from '../../../store/actions/AddToCollection'

class PostCerdits extends Component {
  constructor() {
    super()
    this.state = {}
  }

  // incrementing like to the post
  incrementLike = () => {
    let likes = this.props.likeCount + 1
    this.setState(() => {
      return {
        likeToPost: this.props.project.id,
        likeCount: likes
      }
    }, () => {
      this.props.createLike(this.state)
    })
  }

  // adding post to user's collection
  addCollection = () => {
    let collectedCount = this.props.collected + 1
    let activeStatus = this.props.isActive ? false : true
    this.setState(() => {
      return {
        imageUrl: this.props.project.imageUrl,
        postId: this.props.project.id,
        activeStatus,
        collectedCount
      }
    }, () => {
      this.props.addCollection(this.state)
    })
  }

  render() {
    const { likeCount } = this.props
    // console.log(this.props.project.imageUrl)
    return(
      <div>
      <div className="icons-wrapper">
        <div className="post-act">
          <span className="">
            <button onClick={this.incrementLike}>
              <i className="material-icons">thumb_up</i>
            </button>
          </span>
          <span className="">
            <button>
              <i className="material-icons">send</i>
            </button>
          </span>
        </div>
        <div className="save">
          <span className="">
            <button onClick={this.addCollection}>
              <i className="material-icons center"
                  title="add to your collection">
                add_to_photos
              </i>
            </button>
          </span>
        </div>
      </div>

          {likeCount
            ?
              <div className="counts">
                <span className="like-count">
                  {likeCount} likes
                </span>
              </div>
            : null
          }
      </div>
    )
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    createLike: (like) => dispatch(createLike(like)),
    addCollection: (post) => dispatch(addCollection(post))
  }
}


export default connect(null, mapDispatchToProps)(PostCerdits)
