import React, { Component } from 'react'
import ProjectList from '../projects/ProjectList'
import { connect } from 'react-redux'
import { compose } from 'redux'
import { firestoreConnect } from 'react-redux-firebase'
import { Redirect } from 'react-router-dom'
import Notification from './Notification'
// import Footer from './Footer'
import Spinner from './Spinner'
// import { getData, allPosts } from '../../utils/GetSortedPost' // retrieving data from cloud firestore
// import { passPostToStore } from '../../store/actions/postsToStore'

class Dashboard extends Component {
  constructor() {
    super()
    this.state = {}
  }

  // TODO: get the posts manually and dispatch it
  // to the store
  // TODO: set data to local storage once component
  // successfully mounted

  // componentWillMount() {
  //   if(!this.state.projects && !allPosts.length > 0) {
  //     console.log("all posts: ", allPosts)
  //     getData()
  //     .then(() => {
  //       console.log("see", allPosts)
  //       this.setState(() => {
  //         return{
  //           projects: [...allPosts]
  //         }
  //       })
  //     })
  //     .catch(error => {
  //       console.log("error retrieving data")
  //     })
  //   } else {
  //     this.setState(() => {
  //       return {
  //         projects: [...allPosts]
  //       }
  //     })
  //     console.log("state is not empty")
  //   }
  //   console.log("comp", this.state)
  // }

  render() {
  //  console.log("project: ", this.state)
    const { projects, auth, notifications } = this.props
    if (!auth.uid) return <Redirect to='/signin' /> // redirecting signed out users to signin/signup page

    if(projects && projects.length < 0) return <Spinner />
    return(
      <div className="dashboard container">
        <div className="row">
          <div className="col l8 m12 s12">
            <ProjectList projects={projects} auth={auth}/>
          </div>
          <div className="col l4 hide-on-med-and-down">
            <Notification notifications={notifications} />
          </div>
        </div>
        <div className="footer-cover">
        </div>
      </div>
    )
  }
}

const mapStateToProps = (state) => {
  // console.log(state)
  return {
    projects: state.firestore.ordered.projects,
    initials: state.firebase.profile.initials,
    auth: state.firebase.auth,
    notifications: state.firestore.ordered.notifications
  }
}

export default compose (
  firestoreConnect([
      { collection: 'projects',
        orderBy: ['createdAt', 'desc']
      },
      { collection: 'notifications',
        limit: 3,
        orderBy: ['time', 'desc']
      }
    ]),
  connect(mapStateToProps)
)(Dashboard)
