import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Redirect } from 'react-router-dom'
import portrait from '../../static/portrait.JPG'

class Profile extends Component {
  constructor() {
    super()
    this.state = {

    }
  }
  render() {
    const { auth } = this.props
    const { profile } = this.props
    if(!auth.uid) return <Redirect to="/signin" />
    return(
      <div className="container">
        <div className="row">

          <div className="profile col l10 offset-l1 m10 offset-m1 s12 white">
            <div className="row">
              <div className="col l6 offset-l4 m6 offset-m4 s12 offset-s3">
                <div className="profile-image center-align">
                  <img src={portrait} alt="portrait" className="circle responsive-img" height="200" width="200" />
                  <span className="user-name"> {`${profile.firstName} ${profile.lastName}`}  </span>
                </div>
              </div>
            </div>
          </div>

          <div>

          </div>

        </div>
      </div>
    )
  }
}

const mapStateToProps = (state) => {
  console.log("for: ", state)
  return {
    auth: state.firebase.auth,
    profile: state.firebase.profile
  }
}

export default connect(mapStateToProps)(Profile)
