import React, { Component } from 'react'
import portrait from '../../../static/portrait.png'

class EditProfile extends Component {
  constructor() {
    super()
    this.state = {}
  }

  // trigger input/file
  handleTrigger = () => {
    const ref = this.refs.picture
    ref.click()
  }

  // hanlde file change
  handleFile = (event) => {
    event.persist()
    console.log("sdfsd")
    const input = event.target;
    const imageOriginalName = input.files[0].name
    const reader = new FileReader()
     reader.onload = () => {
       const dataURL = reader.result
       console.log("img: ", dataURL)

       this.setState(() => {
         return {
           imageSrc: dataURL,
           imageName: imageOriginalName
         }
       }, () => {
         console.log("state: ", this.state)
       })
     }
     reader.readAsDataURL(input.files[0])
  }

  render() {
    const { profile, auth } = this.props

    return(
      <div className="modal_wrapper">
        <div className="modal_cont white">
          <div className="edit_pic">
            <div>
                <i className="material-icons" onClick={this.handleTrigger}>create</i>
              { profile && profile.pictureUrl
                ? <img src={profile.pictureUrl} alt="dp" height="150px" width="150px" />
                : <img src={portrait} alt="dp" height="150px" width="150px" /> }
            </div>
            <form action="">
              <input
                type="file"
                ref="picture"
                onChange={this.handleChange}  />
            </form>
          </div>
          <div>

          </div>
        </div>
      </div>
    )
  }
}

export default EditProfile
