import React from "react"
import portrait from '../../../static/portrait.png'

const Picture = ({profile, onClick, paramId}) => {

  const handleClick = () => {
    onClick()
  }

  return (
    <div>
      { paramId === auth.uid
        ? <div className="edit_pro_wrap">
            <span onClick={handleClick}>
              {/*<i className="material-icons"> edit </i>*/}
              edit profile
            </span>
          </div>
        : null }
      <div className="picture">
        { profile && profile.pictureUrl
          ? <img src={profile.pictureUrl} alt="dp" />
          : <img src={portrait} alt="dp" /> }

          <div className="user_basic">
            <div>
              <span className="full_name">
                {`${profile.firstName} ${profile.lastName}`}
              </span>
              <span> user name </span>
            </div>
          </div>
      </div>
    </div>
  )
}

export default Picture
