import React, { Component } from 'react'
import { connect } from 'react-redux'
import { compose } from 'redux'
import { firestoreConnect } from 'react-redux-firebase'
import 'firebase/firestore'
import portrait from '../../static/portrait.png'
import coffee from '../../static/coffee.jpg'
import ProfileTabs from './ProfileTabs'
import UploadImage from './UploadImage'

class ProfilePicture extends Component {
  constructor() {
    super()
    this.state = {
      style: {
        display: "none"
      }
    }
  }

  getTab = (item) => {
    this.props.onClick(item)
  }

  handleClick = () => {
    this.setState(() => {
      return{
        style: {
          display: "block"
        }
      }
    })
  }

  closeModal = () => {
    this.setState(() => {
      return{
        style: {
          display: "none"
        }
      }
    })
  }

  // get user profile picture data



  render() {
    const { pictureUrl } = this.props
    return(
      <div>
        <div className="profile-picture">

          <div style={this.state.style}>
            <UploadImage onClick={this.closeModal} uid={this.props.uid}/>
          </div>

          <div className="cover">
            <img src={coffee} alt="cover" height="300px" width="100%" />
            <i className="material-icons">add_a_photo</i>
          </div>

          <div className="container">
            <div className="row">

              <div className="col l4 pull-l1 m4 pull-m1 s8 push-s2 center profile-about-cover">

                <div className="image">
                  {
                    this.props.user && this.props.user.pictureUrl
                    ? <img src={this.props.user.pictureUrl} alt="profile" />
                    : <img src={portrait} alt="profile" />
                  }
                  <i className="material-icons"
                    onClick={this.handleClick}>
                    add_a_photo
                  </i>
                </div>

              </div>

              <div className="col l8 m8 s12">
                <div>
                  <ProfileTabs onClick={this.getTab} />
                </div>
              </div>

            </div>
          </div>

        </div>
      </div>
    )
  }
}

const mapStateToProps = (state) => {
  console.log("user: ", state)
  let user = state.firestore && state.firestore.ordered
    && state.firestore.ordered.user && state.firestore.ordered.user.pictureUrl
    ? state.firestore.ordered.user.pictureUrl
    : null
    // console.log("user: ", user)
  return {
    pictureUrl: user
  }
}

export default compose(
  firestoreConnect(props => {
    const id = props.uid
    return[
      {
        collection: 'users',
        doc: id,
        storeAs: 'user'
      }
    ]
  }),
  connect(mapStateToProps)
)(ProfilePicture)

// export default ProfilePicture
