import React, { Component } from 'react'
import { connect } from 'react-redux'
// import CommentReducer from '../../../store/actions/CommentReducer'

class AddComment extends Component {
  constructor() {
    super()
    this.state = {
      comment: '',
      commentFrom: '',
      commentToPost:''
    }
  }

  handleSubmit = (event) => {
    event.preventDefault()
    console.log("before: ", this.state)
    this.setState(() => {
      return {
        comment: this.refs.comment.value,
        commentFrom: ` ${this.props.profile.firstName} ${this.props.profile.lastName}`,
        commentToPost: this.props.projectId
      }
    }, () => {
      console.log("after: ", this.state)
    })
  }

  render() {
    return(
      <div className="comments">
        <form action="" onSubmit={this.handleSubmit}>
          <input type="text" ref="comment" placeholder="add your comment..." className="" />
          <button type="submit" className="btn transparent z-depth-0"></button>
        </form>
      </div>
    )
  }
}

const mapStateToProps = (state) => {
  console.log("profile: " ,state.firebase.profile)
  return {
    profile: state.firebase.profile,
    auth: state.firebase.auth
  }
}

export default connect(mapStateToProps)(AddComment)
