import React from 'react'

const Comments = ({projectId, comments}) => {
  // console.log("comments: ", comments)
  return(
    <div>
      <div className="loaded-comments">
        {comments && comments.map((comment) => {
          return (
            <div key={comment.id}>
              <p>
                <span className="commentor grey-text lighten-3">
                  {comment.commentFrom}
                </span>
                {"  "+comment.comment}
              </p>
            </div>
          )
        })}
      </div>
    </div>
  )
}

