      //           posts: {
      //             ...post
      //           }
      //         }
      //       })
      //     })
      //   })
    return followingRef.get().then(documentSnapshots => {
      documentSnapshots.forEach(doc => {

        // retrieving "following" id to get related posts
        let data = doc.data()
        let uid = data.userid
        console.log("following: ", data, uid)

        // getting posts of "folowing users"
        return db.collection('projects')
          .where("authorId", "==", "IAjgGx0n2UPbmrl9scG9ocjOZCM2")
          .orderBy('createdAt', 'desc')
          .limit(3)
          .get().then(querySnapshots => {

            querySnapshots.forEach((docs) => {
              let obj = {
                ...docs.data(),
                id: docs.id
              }
              post.push(obj)
              this.setState(() => {
                return {
                  posts: {
                    ...post
                  }
                }
              })
            })
          })
      })
    })

  }

  render() {
  // console.log("project: ", this.state)
    console.log("st: ", this.state)
    const { projects, auth, notifications } = this.props
    if (!auth.uid) return <Redirect to='/signin' /> // redirecting signed out users to signin/signup page

    if(projects && projects.length < 0) return <Spinner />
    return(
      <div className="dashboard container">
        <div className="row">
          <div className="col l8 m12 s12">
            {projects
              ? <ProjectList projects={projects} auth={auth}/>
              : null}
          </div>
          <div className="col l4 hide-on-med-and-down">
            <Notification notifications={notifications} />
          </div>
        </div>
        <div className="footer-cover">
        </div>
      </div>
    )
  }
}

const mapStateToProps = (state) => {
  // console.log(state)
  return {
    projects: state.firestore.ordered.projects,
    initials: state.firebase.profile.initials,
    auth: state.firebase.auth,
    notifications: state.firestore.ordered.notifications
  }
}

export default compose (
  firestoreConnect([
      { collection: 'projects',
        orderBy: ['createdAt', 'desc']
      },
      { collection: 'notifications',
        limit: 3,
        orderBy: ['time', 'desc']
      }
    ]),
  connect(mapStateToProps)
)(Dashboard)
