import React, { Component } from 'react'
import firebase from 'firebase/app'
import ReactCrop from 'react-image-crop'
import 'react-image-crop/dist/ReactCrop.css';
import portrait from '../../static/portrait.png'

class UploadImage extends Component {
  constructor() {
    super()
    this.state = {
      imageSrc: portrait,
      crop: {
        aspect: 1/1
      }
    }
  }

  handleChange = (crop) => {
    this.setState({ crop });
  }

  handleClick = () => {
    this.props.onClick()
  }

  handleFile = (event) => {
    event.persist()
    // console.log("i:\n", event.target.files[0])
    const myFileReader = new FileReader()
    myFileReader.addEventListener("load", () => {
    const final =  myFileReader.readDataAsUrl(event.target.files[0])
    console.log(final)
    })
    this.setState(() => {
      return {
        imageSrc: event.target.files[0]
      }
    })
  }

  render() {
    return(
      <div className="modal-wrapper">
        <div className="close right" onClick={this.handleClick}>
          <span className="white-text">close</span>
            <i className="material-icons white-text">
              close
            </i>
        </div>
        <div className="image-modal">
          <div className="photo">
            {/*<img src={this.state.imageSrc}
              alt="dp"
              className="responsive-img"/>*/}
            <ReactCrop src={this.state.imageSrc} crop={this.state.crop} onChange={this.handleChange} />
          </div>
          <div className="select-file center">
            <form>
              <input type="file" onChange={this.handleFile} />
              <button type="submit" className="btn indigo z-depth-0">
                Upload
              </button>
            </form>
          </div>
        </div>
      </div>
    )
  }
}

export default UploadImage
