import React, { Component } from 'react'
import Picture from './Components/Picture'
import Quote from './Components/Quote'
import Tabs from './Tabs'
import UserDetails from './Components/UserDetails'
import Collection from './Components/Collection'
import Artwork from './Components/Artwork'
import Bio from './Components/Bio'

class ProfileContent extends Component {
  constructor() {
    super()
    this.state = {}
  }

  handleClick = (tab) => {
    this.setState(() => {
      return {
        selectedTab: tab
      }
    }, () => {
      console.log(this.state)
    })
  }

  displayModal = () => {
    this.props.onClick()
  }

  render() {
    const {profile, auth, artWorks} = this.props
    // console.log("auth", profile)
    let currentTab = this.state.selectedTab
    let tab

    if(currentTab === 'Collection') {
      tab = <Collection auth={auth} />
    } else if(currentTab === 'Bio') {
      tab = <Bio />
    } else {
      tab = <Artwork artWorks={artWorks} auth={auth}  />
    }

    return(
      <div>
        <div className="col l6 m7 s12">
          <div className="card z-depth-0 white height210">
            <div className="profile_basic_cover">
              { profile
                ? <Picture
                    profile={profile}
                    auth={auth}
                    onClick={this.displayModal}/>
                : null }
            </div>
                  <div className="devider"></div>
            <div className="tab_sec">
              <Tabs onClick={this.handleClick} />
            </div>
          </div>
          <div>
            <UserDetails />
          </div>
          <div className="hide-on-med-and-up quote_use_det">
            {tab}
          </div>
        </div>

        <div className="col l6 m5 s12 hide-on-small-only quote_use_det">
          <Quote />
          {tab}
        </div>
      </div>
    )
  }
}
export default ProfileContent
