import React, { Component } from 'react'
// import firebase from 'firebase/app'
import ReactCrop from 'react-image-crop'
import 'react-image-crop/dist/ReactCrop.css';
import portrait from '../../static/portrait.png'
import {base64StringtoFile,
  downloadBase64File,
  extractImageFileExtensionFromBase64,
  image64toCanvasRef} from '../../utils/CropOutput'

class UploadImage extends Component {
  constructor() {
    super()
    this.imagePreviewCanvasRef = React.createRef()
    this.fileInputRef = React.createRef()
    this.state = {
      imageSrc: portrait,
      crop: {
        aspect: 1/1
      }
    }
  }

  handleFile = (event) => {
    event.persist()
    const input = event.target;
    const imageOriginalName = input.files[0].name
     const reader = new FileReader()
     reader.onload = () => {
       const dataURL = reader.result
       // console.log("img: ", dataURL)

       this.setState(() => {
         return {
           imageSrc: dataURL,
           imgSrcExt: extractImageFileExtensionFromBase64(dataURL),
           imageName: imageOriginalName
         }
       })
     }
     reader.readAsDataURL(input.files[0])
  }

  handleClick = () => {
    this.props.onClick()
  }

  handleChange = (crop) => {
    this.setState({ crop }, () => {
      console.log(this.state)
    })
  }

  handleOnCropComplete = (crop, pixelCrop) => {
    //console.log(pixelCrop)
    const canvasRef = this.imagePreviewCanvasRef.current
    const {imgSrc}  = this.state
    image64toCanvasRef(canvasRef, imgSrc, pixelCrop)
  }

  handleSubmit = (e) => {
    e.preventDefault()
    const { imageSrc } = this.state
    if(imageSrc) {
      const canvasRef = this.imagePreviewCanvasRef.current

      const {imgSrcExt} =  this.state
      const {imageOriginalName} = this.state
      const imageData64 = canvasRef.toDataURL('image/' + imgSrcExt)

      const myFilename = imageOriginalName

      // file to be uploaded
      const myNewCroppedFile = base64StringtoFile(imageData64, myFilename)
      console.log("cropped new: ", myNewCroppedFile)
      // download file
      // downloadBase64File(imageData64, myFilename)
    }
  }

  render() {
    return(
      <div className="modal-wrapper">
        <div className="close right" onClick={this.handleClick}>
          <span className="white-text">close</span>
            <i className="material-icons white-text">
              close
            </i>
        </div>
        <div className="image-modal">
          <div className="photo">
            {/*<img src={this.state.imageSrc}
              alt="dp"
              className="responsive-img"/>*/}
            <ReactCrop
              className="responsive-img"
              src={this.state.imageSrc}
              crop={this.state.crop}
              onChange={this.handleChange}
              onComplete={this.handleOnCropComplete}/>
              <br/>
              <canvas ref={this.imagePreviewCanvasRef}></canvas>

          </div>
          <div className="select-file center">
            <form>
              <input type="file" onChange={this.handleFile} />
              <button type="submit"
                className="btn indigo z-depth-0"
                onClick={this.handleSubmit}>
                Upload
              </button>
            </form>
          </div>
        </div>
      </div>
    )
  }
}

export default UploadImage
