import React, { Component } from 'react'
import { firestoreConnect } from 'react-redux-firebase'
import { compose } from 'redux'
import { connect } from 'react-redux'
import firebase from 'firebase/app'
import 'firebase/firestore'
import { createLike, removeLike } from '../../../store/actions/LikeActions'
import { addCollection } from '../../../store/actions/AddToCollection'

class PostCerdits extends Component {
  constructor() {
    super()
    this.state = {}
  }

  componentWillMount() {
    const db = firebase.firestore()
    const ref = db.collection('projects').doc(this.props.project.id)
      .collection('likes')
      .where('likeFromId', '==', this.props.project.authorId)

    return ref.get().then((documentSnapshots) => {
      documentSnapshots.forEach(doc => {
        this.setState({
          like: doc.data()
        }, () => {
          console.log("thisss: ", this.state.like)
        })
      })
    })
  }

