  }
}

export default compose (
  connect(mapStateToProps),
  firestoreConnect(props => {
    return [
      { collection: 'projects',
        doc: props.id,
        subcollections: [
          {
            collection: 'comments',
            storeAs: 'comments'
          }
        ]
      }
    ]
  })
)(Comment)

