import React, { Component } from 'react'
import portrait from '../../static/portrait.png'
import coffee from '../../static/coffee.jpg'
import ProfileTabs from './ProfileTabs'
import UploadImage from './UploadImage'

class ProfilePicture extends Component {
  constructor() {
    super()
    this.state = {
      style: {
        display: "none"
      }
    }
  }

  getTab = (item) => {
    this.props.onClick(item)
  }

  handleClick = () => {
    this.setState(() => {
      return{
        style: {
          display: "block"
        }
      }
    })
  }

  closeModal = () => {
    this.setState(() => {
      return{
        style: {
          display: "none"
        }
      }
    })
  }

  render() {
    const { profile } = this.props
    const { urlId } = this.props
    console.log(urlId)
    return(
      <div>
        <div className="profile-picture">

          <div style={this.state.style}>
            <UploadImage onClick={this.closeModal} uid={this.props.uid}/>
          </div>

          <div className="cover">
            <img src={coffee} alt="cover" height="300px" width="100%" />
            <i className="material-icons">add_a_photo</i>
          </div>

          <div className="container">
            <div className="row">

              <div className="col l4 pull-l1 m4 pull-m1 s8 push-s2 center profile-about-cover">

                <div className="image">
                  <div>
                  {
                    profile
                    ? <img src={profile.pictureUrl} alt="profile" />
                    : <img src={portrait} alt="profile" />
                  }                       
                  </div>
                  <i className="material-icons"
                    onClick={this.handleClick}>
                    add_a_photo
                  </i>
                </div>

              </div>

              <div className="col l8 m8 s12">
                <div>
                  <ProfileTabs onClick={this.getTab} />
                </div>
              </div>

            </div>
          </div>

        </div>
      </div>
    )
  }
}

export default ProfilePicture
