import React, { Component } from 'react'
import { connect } from 'react-redux'
import { compose } from 'redux'
import { firestoreConnect } from 'react-redux-firebase'
import { Link } from 'react-router-dom'
import Arts from './Arts'
// import MobileNavbar from '../layout/MobileNavbar'
// import StoreIntro from './StoreIntro'
import StoreNav from './StoreNav'

class Store extends Component {
    constructor() {
        super()
        this.state = {
            style: {
                display: 'none'
            }
        }
    }

    closeImage = () => {
        this.setState({style:{display:'none'}})
        document.getElementById("body").style.overflow = 'auto';
    }

    displayImage = (art) => {
        this.setState({
            style: {
                display:'block'
            },
            currentImage: art
        })
        document.getElementById("body").style.overflow = 'hidden';
    }

    render() {
        // console.log(auth)
        const { auth } = this.props
        return (
            <div className="store_wrapper">
                    <div className="display_image" style={this.state.style}>
                        <div>
                            <img src={this.state.currentImage} alt="overview" />
                        </div>
                        <i 
                            className="material-icons"
                            onClick={this.closeImage}> close </i>
                    </div>
                <div className="store">
                    <StoreNav />
                    <div className="row store_owner">
                        <div className="col l6 m6 s12 store_details">
                            <i className="material-icons"> store </i>
                            <div>
                                <span> Store Name </span>
                                <span className="user_profile">
                                    ColorStack Profile {'  '}
                                    <Link to={`/profile/${auth.uid}`}>
                                        {auth && (auth.displayName || auth.fullName || `${auth.firstName} ${auth.lastName}`)}
                                    </Link>
                                </span>
                            </div>
                        </div>
                        <div className="col l12 m12 s12 selling_arts">
                            <Arts onClick={this.displayImage} />
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return{
        auth: state.firebase.auth
    }
}

export default compose(
    firestoreConnect([

    ]),
    connect(mapStateToProps)
)(Store)
